    def is_safe(subsets):
        for i in range(k):
            for j in range(i + 1, k):
                if not subsets[i].isdisjoint(subsets[j]):
                    return False
        return True

    def is_progression(subset):
        if len(subset) < 3:
            return False
        d = subset[1] - subset[0]
        for i in range(2, len(subset)):
            if subset[i] - subset[i - 1] != d:
                return False
        return True

    def is_complete(subsets):
        if not is_safe(subsets):
            return False
        words = set(range(1, n + 1))
        for subset in subsets:
            words -= subset
        return not words

    subsets = [set() for _ in range(k)]
    for i in range(n):
        for j in range(k):
            subsets[j].add(i + 1)
            if not is_progression(subsets[j]):
                subsets[j].remove(i + 1)
                break
    if is_complete(subsets):
        return "".join(str(i + 1) for i in range(k))
    else:
        return "-1"


