def solution(stdin: str) -> str:
    def find_distance(graph: list, start: int, end: int) -> int:
        queue = [(start, 0)]
        visited = set()
        while queue:
            current, dist = queue.pop(0)
            if current == end:
                return dist
            for neighbor, weight in graph[current]:
                if neighbor not in visited:
                    visited.add(neighbor)
                    queue.append((neighbor, dist + weight))
        return -1

    def attack_base(spaceships: list, bases: list, dummy_bases: list) -> int:
        min_cost = 0
        for ship in spaceships:
            min_cost_for_ship = float('inf')
            for base in bases + dummy_bases:
                if ship[0] != base[0] and ship[2] >= find_distance(graph, ship[0], base[0]):
                    attack_cost = ship[1] * min(base[1], ship[1]) + ship[2] * find_distance(graph, ship[0], base[0])
                    min_cost_for_ship = min(min_cost_for_ship, attack_cost)
            min_cost += min_cost_for_ship
        return min_cost

    # Read input
    lines = stdin.splitlines()
    n, m = map(int, lines[0].split())
    graph = [[] for _ in range(n)]
    for i in range(1, n):
        graph[i].append((i - 1, 1))
    for i in range(1, m):
        u, v = map(int, lines[i].split())
        graph[u - 1].append((v - 1, 1))
        graph[v - 1].append((u - 1, 1))
    s, b, k, h = map(int, lines[m].split())
    spaceships = []
    bases = []
    for i in range(m + 1, m + s):
        x, a, f = map(int, lines[i].split())
        spaceships.append((x - 1, a, f))
    for i in range(m + s + 1, m + s + b):
        x, d = map(int, lines[i].split())
        bases.append((x - 1, d))

    # Create dummy bases
    for i in range(b // 4):
        dummy_bases.append((-1, 0))

    # Calculate minimum cost
    min_cost = attack_base(spaceships, bases, dummy_bases)
    return min_cost

