def solution(stdin: str) -> str:
    n = int(stdin.splitlines()[0])
    p = [int(x) for x in stdin.splitlines()[1].split()]

    def count_conveniences(p):
        count = 0
        for i in range(n):
            for j in range(n):
                if p[i] == j + 1:
                    count += (i + 1) * (j + 1) // 2
                    break
        return count

    def count_conveniences_with_changes(p):
        count = 0
        for i in range(n):
            j = p[i] - 1
            if j != i:
                count += i * j + (n - i - 1) * (n - j - 1) // 2
        return count

    return max(count_conveniences(p), count_conveniences_with_changes(p))


