def solution(stdin: str) -> str:
    n, m, c_l, c_e, v = map(int, stdin.split()[:5])
    l_pos = list(map(int, stdin.split()[5:5+c_l]))
    e_pos = list(map(int, stdin.split()[5+c_l:]))
    q = int(stdin.split()[-1])
    queries = [tuple(map(int, input().split())) for _ in range(q)]

    def dist(p1, p2):
        return abs(p1[0] - p2[0]) + abs(p1[1] - p2[1])

    def elevator_time(p1, p2):
        return dist(p1, (1, p1[1])) + dist((1, p1[1]), p2)

    def stairs_time(p1, p2):
        return dist(p1, p2) + 1

    def answer_query(q):
        x1, y1, x2, y2 = q
        if x1 == x2:
            return dist((x1, y1), (x2, y2))
        if y1 == y2:
            return dist((x1, y1), (x2, y2))
        if x1 < x2:
            if y1 < y2:
                return min(elevator_time((x1, y1), (x2, y2)), stairs_time((x1, y1), (x2, y2)), stairs_time((x1, y2), (x2, y2)))
            else:
                return min(elevator_time((x1, y1), (x2, y2)), stairs_time((x1, y1), (x2, y2)), stairs_time((x1, y2), (x2, y2))) + v - 1
        else:
            if y1 < y2:
                return min(elevator_time((x1, y1), (x2, y2)), stairs_time((x1, y1), (x2, y2)), stairs_time((x2, y1), (x2, y2))) + v - 1
            else:
                return min(elevator_time((x1, y1), (x2, y2)), stairs_time((x1, y1), (x2, y2)), stairs_time((x2, y1), (x2, y2))) + 2 * v - 2

    answers = [answer_query(q) for q in queries]
    return '\n'.join(map(str, answers))

