    if not is_prime(n):
        return "Error: n must be a prime number"

    def angle(n):
        return math.pi / n

    def side_length(n, r):
        return 2 * r * math.sin(angle(n) / 2)

    def area(n, r):
        return n * (side_length(n, r) ** 2) / (4 * math.tan(angle(n) / 2))

    return f"{area(n, r):.10f}"


