    n, m = map(int, input().split())
    house = [input() for _ in range(n)]

    def get_direction(x, y):
        if x > 0 and house[x - 1][y] == '+':
            return 'left'
        elif x < n - 1 and house[x + 1][y] == '+':
            return 'right'
        elif y > 0 and house[x][y - 1] == '+':
            return 'up'
        elif y < m - 1 and house[x][y + 1] == '+':
            return 'down'
        else:
            return 'unknown'

    def move(x, y, direction):
        if direction == 'right':
            return x, y + 1
        elif direction == 'left':
            return x, y - 1
        elif direction == 'up':
            return x - 1, y
        elif direction == 'down':
            return x + 1, y
        else:
            return x, y

    def hit_wall(x, y, direction):
        next_x, next_y = move(x, y, direction)
        if house[next_x][next_y] == '#':
            house[next_x][next_y] = '+'
        return next_x, next_y

    time = 0
    x, y = 0, 0

    while x != n - 1 or y != 0:
        direction = get_direction(x, y)
        time += 1

        if direction == 'unknown':
            break
        else:
            next_x, next_y = move(x, y, direction)

            if house[next_x][next_y] == '.':
                x, y = next_x, next_y
            else:
                if house[next_x][next_y] == '+':
                    hit_wall(x, y, direction)
                x, y = next_x, next_y
                direction = 'left' if direction == 'right' else 'right'
                time += 1

    return time if x == n - 1 and y == 0 else 'Never'


