        path_penalty = 0

        def is_valid_move(i, j, history):
            for x, y in history:
                if abs(i - x) + abs(j - y) == 1:
                    return False
            return True

        def backtrack(i, j, history):
            nonlocal candy_placed, path_penalty

            if i == 1 and j == 1:
                if candy_placed:
                    paths.append(path)
                else:
                    penalty += len(path)
                    candy_placed = True
                return

            if i < 1 or i > n or j < 1 or j > m:
                return

            if not is_valid_move(i, j, history):
                return

            history.append((i, j))
            path.append((i, j))
            path_penalty += 1

            if not candy_placed:
                backtrack(i - 1, j, history)
                backtrack(i, j - 1, history)

            history.pop()
            path.pop()
            path_penalty -= 1

        backtrack(n, m, [])

    print(penalty)
    for path in paths:
        print(" ".join(f"({i},{j})" for i, j in path))

