    N = int(stdin.split()[0])
    probabilities = [[0 for _ in range(2**N)] for _ in range(2**N)]

    for i in range(2**N):
        for j in range(2**N):
            probabilities[i][j] = int(stdin.split()[i+1].split()[j+1])

    def calculate_expected_score(bracket: list, multiplier: int, round_num: int) -> float:
        if round_num == N:
            return sum(bracket)
        else:
            winners = [i for i in range(2**(round_num+1)) if bracket[i] == 1]
            expected_scores = [0 for _ in range(2**(round_num+1))]
            for winner in winners:
                for game in range(2**(round_num+1)):
                    if winner in probabilities[game]:
                        prob_win_against_opponent = probabilities[game][winner]
                    else:
                        prob_win_against_opponent = 0
                    expected_scores[game] += prob_win_against_opponent * multiplier
            return sum(expected_scores)

    optimal_bracket = [1 for _ in range(2**N)]
    for round_num in range(N, 0, -1):
        multiplier = 2**(N-round_num)
        current_round_winners = [i for i in range(2**round_num)]
        for winner in current_round_winners:
            for opponent in range(2**round_num):
                if winner == opponent:
                    continue
                for game in range(2**(round_num+1)):
                    if winner in probabilities[game] and opponent in probabilities[game]:
                        prob_win_against_opponent = probabilities[game][winner]
                        prob_lose_to_opponent = probabilities[game][opponent]
                        prob_win_overall = prob_win_against_opponent / (prob_win_against_opponent + prob_lose_to_opponent)
                        if prob_win_overall > probabilities[game][opponent]:
                            probabilities[game][opponent] = prob_win_overall
                            probabilities[game][winner] = 1 - prob_win_overall

        optimal_bracket_round = [1 for _ in range(2**(round_num+1))]
        for game in range(2**(round_num+1)):
            if optimal_bracket[game] == 1:
                optimal_bracket_round[game] = 1
            elif optimal_bracket[game] == 0:
                optimal_bracket_round[game] = 0
            else:
                optimal_bracket_round[game] = probabilities[game][optimal_bracket[game]]

        optimal_bracket = optimal_bracket_round

    return round(calculate_expected_score(optimal_bracket, 1, 1), 9)


