    n, m = map(int, stdin.split()[0:2])
    data = [list(map(int, line.split()[0:3])) for line in stdin.split('\n')[1:] if line]

    def debt(a, b):
        for u, v, d in data:
            if u == a and v == b:
                return d
        return 0

    def debts_after_consolidation(n, data):
        for a, b, d in data:
            if debt(a, a) > 0:
                return 1, [(a, a, debt(a, a))]
        return n, data

    def find_optimal_sequence(n, m, data):
        def total_debt(data):
            return sum(d for u, v, d in data)

        def update_data(data, a, b, c, d, z):
            return [(u, v, d) if u != a or v != b else (u, v, d + z) for u, v, d in data]

        def consolidate_debts(n, data, a, b, c, d, z):
            new_data = update_data(data, a, b, c, d, z)
            return debts_after_consolidation(n, new_data)

        def debts_after_operation(n, data, a, b, c, d, z):
            if debt(a, b) == 0:
                return n, data

            new_n, new_data = consolidate_debts(n, data, a, b, c, d, z)
            if new_n == 1:
                return new_n, [(c, a, d)]

            new_n, new_data = debts_after_operation(new_n, new_data, c, a, b, d, z)
            if new_n == 1:
                return new_n, [(a, b, d)]

            return debts_after_consolidation(n, data)

        def find_optimal_sequence_helper(n, m, data, debt_sum):
            if n == 1:
                return n, data

            for a, b, d in data:
                for c, d, z in [(a, b, d // 2), (a, b, d - 1), (a, b, d + 1)]:
                    if z > 0 and z < d and d - z < d - 1:
                        new_n, new_data = debts_after_operation(n, data, a, b, c, d, z)
                        if new_n == 1:
                            new_debt_sum = debt_sum + d - z
                            if new_debt_sum < debt_sum:
                                return find_optimal_sequence_helper(new_n, m, new_data, new_debt_sum)

        return find_optimal_sequence_helper(n, m, data, total_debt(data))

    n, data = debts_after_consolidation(n, data)
    n, data = find_optimal_sequence(n, m, data)

    return n, data

