        path.append(u)
        for i, w in enumerate(adj_list[u]):
            if w == v:
                paths.append(path.copy())
                path.pop()
                return
            if not visited[w]:
                bfs(w, v, visited, paths, path)
                path.pop()

    paths = []
    for u in range(n):
        for v in range(u + 1, n):
            if visited[u] and visited[v]:
                continue
            visited = [False] * n
            bfs(u, v, visited, paths, [])

