        queue = [(1, 1)]
        visited = set()
        while queue:
            x, y = queue.pop(0)
            if (x, y) == (n, n):
                break
            for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
                new_x, new_y = x + dx, y + dy
                if 1 <= new_x <= n and 1 <= new_y <= n and (new_x, new_y) not in visited:
                    visited.add((new_x, new_y))
                    if grid[new_x - 1][new_y - 1] == '1':
                        queue.append((new_x, new_y))
                    else:
                        queue.insert(0, (new_x, new_y))
        else:
            output.append("0")
            continue

        # Check if the path can be followed with the given digit
        def is_valid(x, y, digit):
            if (x, y) == (n, n):
                return True
            if grid[x - 1][y - 1] != digit:
                return False
            for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
                new_x, new_y = x + dx, y + dy
                if 1 <= new_x <= n and 1 <= new_y <= n and grid[new_x - 1][new_y - 1] == digit:
                    if is_valid(new_x, new_y, digit):
                        return True
            return False

        # Invert cells that can be visited by the given digit
        for x in range(1, n + 1):
            for y in range(1, n + 1):
                if grid[x - 1][y - 1] in ['0', '1'] and is_valid(x, y, grid[x - 1][y - 1]):
                    inverted_cells.add((x, y))

        # Check if there are at most 2 inverted cells
        if len(inverted_cells) > 2:
            output.append("2")
