def solution(stdin: str) -> str:
    def find_path(graph, start, end, path=None):
        if path is None:
            path = []
        path = path + [end]
        if end == start:
            return path
        for node in graph[end]:
            if node not in path:
                newpath = find_path(graph, start, node, path)
                if newpath:
                    return newpath
        return []

    def shortest_path_tree(graph, start):
        visited = set()
        shortest_path = float('inf')
        tree = []

        def dfs(node, path_length):
            nonlocal shortest_path
            nonlocal tree
            visited.add(node)
            path_length += graph[start][node]

            if node not in visited:
                dfs(node, path_length)

            if path_length < shortest_path:
                shortest_path = path_length
                tree = [node]
            elif path_length == shortest_path:
                tree.append(node)

        dfs(start, 0)
        return shortest_path, tree

    n, m = map(int, stdin.split()[:2])
    graph = {i: [] for i in range(1, n + 1)}

    for _ in range(m):
        u, v, w = map(int, stdin.split()[:3])
        graph[u].append((v, w))

    start = int(stdin.split()[-1])

    shortest_path, tree = shortest_path_tree(graph, start)
    edges = [edge for node, edge in enumerate(tree, 1)]

    return shortest_path, ' '.join(map(str, edges))


