    n = int(stdin[0])
    input_table = [line.strip() for line in stdin[1:].split('\n')]

    def parse(truth_table: str) -> str:
        expr = ''
        x, y, z = False, False, False
        for i, char in enumerate(truth_table):
            if char == '1':
                if i % 4 == 0:
                    x = True
                elif i % 2 == 0:
                    y = True
                else:
                    z = True
            elif char == '0':
                if i % 4 == 0 and x:
                    x = False
                elif i % 2 == 0 and y:
                    y = False
                elif z:
                    z = False

        if x:
            expr += 'x'
        if y:
            expr += 'y'
        if z:
            expr += 'z'

        if not expr:
            return ''

        def or_expr(x, y):
            nonlocal expr
            if y and '!' not in expr:
                return '(x|y)'
            else:
                return 'x|y'

        def and_expr(x, y):
            nonlocal expr
            if not x or '!' in expr:
                return ''
            elif not y:
                return '(x&y)'
            else:
                return 'x&y'

        while '!' in expr:
            i = expr.index('!')
            expr = expr[:i] + expr[i+1:]
            x, y, z = not x, not y, not z

        expr = expr.replace('&', 'n').replace('|', 'o')
        expr = expr.replace('x', 'i').replace('y', 'j').replace('z', 'k')
        expr = expr.replace('n', '&').replace('o', '|')
        expr = expr.replace('i', 'x').replace('j', 'y').replace('k', 'z')

        for i in range(len(expr)):
            if expr[i] == '!':
                expr = expr[:i] + expr[i+1:]
            elif expr[i] == '&':
                expr = expr[:i] + and_expr(x, y) + expr[i+1:]
            elif expr[i] == '|':
                expr = expr[:i] + or_expr(x, y) + expr[i+1:]

        return expr

    output = []
    for i, truth_table in enumerate(input_table):
        output.append(parse(truth_table))

    return '\n'.join(output)

