def solution(stdin: str) -> str:
    n, m = map(int, stdin.split()[0:2])
    edges = [tuple(map(int, edge.split())) for edge in stdin.split('\n')[1:] if edge]

    # Create a dictionary to store the incoming edges for each vertex
    incoming_edges = {i: set() for i in range(1, n + 1)}
    for edge in edges:
        incoming_edges[edge[1]].add(edge[0])

    # Create a list of vertices that have no incoming edges
    start_vertices = [i for i in range(1, n + 1) if not incoming_edges[i]]

    # If there are no start vertices, the graph is disconnected
    if not start_vertices:
        raise ValueError("The graph is disconnected")

    # Initialize the labels with start vertices
    labels = {vertex: index + 1 for index, vertex in enumerate(start_vertices)}

    # Find the lexicographically smallest permutation of labels for the remaining vertices
    unassigned_vertices = list(set(range(1, n + 1)) - set(labels.keys()))
    for vertex in unassigned_vertices:
        incoming_edges_for_vertex = incoming_edges[vertex]
        available_labels = [label for label in labels.values() if label < min(incoming_edges_for_vertex)]
        if not available_labels:
            raise ValueError(f"Cannot assign label to vertex {vertex}")
        labels[vertex] = min(available_labels)

    # Print the lexicographically smallest permutation of labels
    return ' '.join(str(labels[i]) for i in range(1, n + 1))

