def solution(stdin: str) -> str:
    D = int(input())
    q = int(input())
    mod = 998244353
    primes = set(range(2, D+1))
    adj = [[] for _ in range(D+1)]

    def get_prime_divisors(n: int) -> set:
        return set(p for p in primes if p <= n and n % p == 0)

    def get_neighbors(n: int) -> set:
        return set(n//d for d in get_prime_divisors(n) if n % d == 0 and d != n)

    def get_weight(x: int, y: int) -> int:
        return len(get_prime_divisors(x) - get_prime_divisors(y))

    for i in range(D):
        adj[i+1] = get_neighbors(i+1)
        for j in adj[i+1]:
            adj[j].add(i+1)

    def dijkstra(v: int) -> int:
        dist = [float('inf')]*(D+1)
        dist[v] = 0
        Q = [i for i in range(D+1)]
        while Q:
            u = min(Q, key=lambda x: dist[x])
            Q.remove(u)
            if dist[u] == float('inf'):
                break
            for v in adj[u]:
                if dist[u] + get_weight(u, v) < dist[v]:
                    dist[v] = dist[u] + get_weight(u, v)
        return dist

    for _ in range(q):
        v, u = map(int, input().split())
        dist_v = dijkstra(v)
        dist_u = dijkstra(u)
        result = 0
        for i in range(1, D+1):
            if dist_v[i] != float('inf') and dist_u[i] != float('inf'):
                result += (dist_v[i] + dist_u[i]) % mod
        print(result % mod)

    return ''.join(solution(input()))

