    def min_transfer(a):
        n = len(a)
        min_moves = [0] * n
        move_count = 0
        for i in range(n):
            if a[i] > 0:
                j = (i - 1) % n
                while j != i and a[j] > 0:
                    a[j] -= 1
                    a[i] -= 1
                    move_count += 1
                    j = (j - 1) % n
                min_moves[i] = move_count
                move_count = 0
            elif a[i] < 0:
                j = (i + 1) % n
                while j != i and a[j] < 0:
                    a[j] += 1
                    a[i] += 1
                    move_count += 1
                    j = (j + 1) % n
                min_moves[i] = move_count
                move_count = 0
        return min_moves

    def min_moves_to_zero(a):
        min_moves = min_transfer(a)
        return sum(min_moves)

    n = int(stdin)
    a = list(map(int, stdin.split()[1:]))
    return min_moves_to_zero(a)

