        n = len(arr)
        swapped = True
        while swapped:
            swapped = False
            for i in range(n - 1):
                if arr[i] > arr[i + 1]:
                    arr[i], arr[i + 1] = arr[i + 1], arr[i]
                    swapped = True
            n -= 1
        return arr

    def max_independent_set(graph):
        independent_set = []
        visited = [False] * n
        for i in range(n):
            if not visited[i]:
                independent_set.append(i)
                visited[i] = True
                for neighbor in graph[i]:
                    if neighbor != i:
                        visited[neighbor] = True
        return independent_set

    def build_graph(arr):
        graph = [[] for _ in range(n)]
        for i in range(n - 1):
            for j in range(i + 1, n):
                if arr[i] > arr[j]:
                    graph[arr[i] - 1].append(arr[j] - 1)
                    graph[arr[j] - 1].append(arr[i] - 1)
        return graph

    graph = build_graph(a)
    bubble_sort(a)
    independent_set = max_independent_set(graph)
    return len(independent_set)

