    def rotate(p, q):
        x, y = p[0] - q[0], p[1] - q[1]
        return -y, x

    def dot(a, b):
        return a[0] * b[0] + a[1] * b[1]

    def norm(a):
        return dot(a, a) ** 0.5

    def cross(a, b):
        return a[0] * b[1] - a[1] * b[0]

    def is_clockwise(p, q, r):
        return cross(p, q) * cross(q, r) > 0

    def stable(p, q, r, x, y):
        p, q, r = rotate(p, q), rotate(q, r), rotate(r, p)
        x, y = rotate(x, y)
        if dot(p, q) > 0 and dot(q, r) > 0 and dot(r, p) > 0:
            return dot(x, p) > 0 and dot(x, q) > 0 and dot(x, r) > 0
        else:
            return dot(x, p) < 0 and dot(x, q) < 0 and dot(x, r) < 0

    def coordinates(p, q, r, x, y):
        p, q, r = rotate(p, q), rotate(q, r), rotate(r, p)
        x, y = rotate(x, y)
        return x[0] - p[0], x[1] - p[1]

