            polygon.append(polygon[t - 1])
            for k in range(len(polygon) - 2):
                if is_clockwise(polygon[k], polygon[k + 1], polygon[k + 2]):
                    for j in range(k + 1, len(polygon) - 1):
                        if stable(polygon[k], polygon[k + 1], polygon[k + 2], polygon[j], (x, y)):
                            polygon[j + 1], polygon[j + 2] = polygon[j + 2], polygon[j + 1]
                    break
            x, y = polygon[-1]
