def solution(stdin: str) -> str:
    def min_cost_chain(graph: list[list[int]]) -> int:
        # Prim's algorithm for finding minimum cost tree
        # https://en.wikipedia.org/wiki/Prim%27s_algorithm
        def create_graph(weights: list[int]) -> list[list[int]]:
            n = len(weights)
            graph = [[] for _ in range(n)]
            for i in range(n):
                for j in range(i + 1, n):
                    graph[i].append((j, weights[i] + weights[j]))
                    graph[j].append((i, weights[i] + weights[j]))
            return graph

        def prim(graph: list[list[int]]) -> list[tuple[int, int]]:
            n = len(graph)
            visited = [False] * n
            mst = []
            visited[0] = True
            dist = [float('inf')] * n
            dist[0] = 0
            for _ in range(n - 1):
                u = -1
                for i in range(n):
                    if not visited[i] and (u == -1 or dist[i] < dist[u]):
                        u = i
                visited[u] = True
                for v, weight in graph[u]:
                    if not visited[v] and weight < dist[v]:
                        dist[v] = weight
                        mst.append((u, v))
            return mst

        graph = create_graph(weights)
        return sum(weight for _, __, weight in prim(graph))

    def is_private(fridges: list[int], n: int, m: int) -> bool:
        for i in range(1, n + 1):
            if sum(1 for u in fridges if u == i) > 1:
                return False
        return len(fridges) == n

    def find_private_fridges(n: int, m: int, weights: list[int]) -> tuple[list[int], int]:
        max_weight = max(weights)
        total_cost = min_cost_chain([[max_weight, max_weight] for _ in range(n * (n - 1) // 2)])
        for i in range(1, n + 1):
            for j in range(i + 1, n + 1):
                if i != j:
                    weights_new = list(weights)
                    weights_new[i - 1] += weights_new[j - 1]
                    weights_new[j - 1] = 0
                    new_total_cost = min_cost_chain([[max_weight, max_weight] for _ in range(n * (n - 1) // 2)])
                    if is_private(weights_new, n, m) and new_total_cost < total_cost:
                        total_cost = new_total_cost
                        private_fridges = weights_new
        return private_fridges, total_cost

    test_cases = stdin.split('\n')
    test_cases.pop(0)
    result = []

    for i in range(0, len(test_cases), 2):
        n, m = map(int, test_cases[i].split())
        weights = list(map(int, test_cases[i + 1].split()))

        private_fridges, total_cost = find_private_fridges(n, m, weights)
        if total_cost == float('inf'):
            result.append(-1)
        else:
            result.append(total_cost)
            for j in range(m):
                u, v = map(int, stdin.split()[2 * j + 2: 2 * j + 4])
                result.append((u, v))
            result.append('')

    return '\n'.join(map(str, result))


