def solution(stdin: str) -> str:
    n, k = map(int, input().split())
    edges = [list(map(int, input().split())) for _ in range(n - 1)]

    # Create the tree using an adjacency list
    tree = [[] for _ in range(n)]
    for a, b in edges:
        tree[a - 1].append(b - 1)
        tree[b - 1].append(a - 1)

    # Initialize the f(s, t) for all pairs (s < t)
    f = [[0] * n for _ in range(n)]
    for i in range(n - 1):
        for j in range(i + 1, n):
            if j in tree[i]:
                f[i][j] = 1

    # Fill the f(s, t) for pairs (s < t) using the triangle inequality
    for mid in range(n - 1):
        for i in range(n):
            for j in range(i + 1, n):
                if f[i][mid] + f[mid][j] <= k:
                    f[i][j] = max(f[i][j], f[i][mid] + f[mid][j])

    # Compute the sum of f(s, t) for all pairs (s < t)
    total = sum(sum(f[i][j] for j in range(i + 1, n)) for i in range(n))

    return str(total)

