    def get_score(trainings, members):
        return max(x * y for x, y in zip(trainings, members))

    def can_reduce_training(member, A_i, K):
        return K - (A_i - 1) >= 0

    def reduce_training(member, A_i, K):
        return A_i - 1, K - (A_i - 1)

    def allocate_dishes(N, A, F, K):
        trainings = [0] * N
        members = list(range(1, N + 1))
        while members:
            dish_assigned = False
            for i, member in enumerate(members):
                if A[member - 1] > 0 and can_reduce_training(member, A[member - 1], K):
                    A[member - 1], K = reduce_training(member, A[member - 1], K)
                    trainings[member - 1] += 1
                    dish_assigned = True
                    break
            if not dish_assigned:
                members.pop(0)
        return trainings, members

    trainings, members = allocate_dishes(N, A, F, K)
    score = get_score(trainings, members)

    return str(score)

