    def lcs(X: str, Y: str) -> str:
        m, n = len(X), len(Y)
        L = [[0] * (n + 1) for _ in range(m + 1)]
        for i in range(m + 1):
            for j in range(n + 1):
                if i == 0 or j == 0:
                    L[i][j] = 0
                elif X[i - 1] == Y[j - 1]:
                    L[i][j] = L[i - 1][j - 1] + 1
                else:
                    L[i][j] = max(L[i - 1][j], L[i][j - 1])
        s = ""
        i, j = m, n
        while i > 0 and j > 0:
            if X[i - 1] == Y[j - 1]:
                s = X[i - 1] + s
                i -= 1
                j -= 1
            elif L[i - 1][j] > L[i][j - 1]:
                i -= 1
            else:
                j -= 1
        return s

    def generate_subsequences(s: str) -> List[str]:
        subsequences = []
        for i in range(len(s)):
            for j in range(i, len(s)):
                subsequences.append(s[i:j + 1])
        return subsequences

    cases = int(stdin.readline())
