        s, t = stdin.readline().strip(), stdin.readline().strip()
        z = ""
        for i in range(len(t)):
            subseqs = generate_subsequences(s)
            if t[i] in subseqs:
                z += t[i]
            else:
                break
        if z == t:
            results.append(len(z))
        else:
            lcs_subseq = lcs(z, t)
            if len(lcs_subseq) == len(t):
                results.append(len(z) + 1)
            else:
                results.append(-1)

    return "\n".join(str(result) for result in results)

