            for col in range(1, n):
                if chessboard[row][col] == 0:
                    b1_pos = (row, col)
                    b2_pos = (n, n)
                    if not (is_bishop_attacked(b1_pos, chessboard) and is_bishop_attacked(b2_pos, chessboard)):
                        money = chessboard[row][col] + chessboard[n-row][n-col]
                        if money > max_money:
                            max_money = money
                            b1_pos = (row, col)
                            b2_pos = (n-row, n-col)
        return max_money, b1_pos, b2_pos

    max_money, b1_pos, b2_pos = get_max_money(chessboard)
