    def total_cleanup(queries, current_set):
        cleanup_cost = 0
        for query in queries:
            if query[0] == 0:
                current_set.remove(query[1])
                current_set.add(query[1] - 1)
            else:
                current_set.add(query[1])

            for pile in sorted(list(current_set)):
                if pile - 1 not in current_set:
                    cleanup_cost += 1
                    current_set.remove(pile)
                    current_set.add(pile - 1)
                if pile + 1 not in current_set:
                    cleanup_cost += 1
                    current_set.remove(pile)
                    current_set.add(pile + 1)

        return cleanup_cost

    initial_set = set(piles)
    answer = total_cleanup(queries, initial_set)
    result = [answer]

    for query in queries:
        if query[0] == 0:
            initial_set.add(query[1] - 1)
            initial_set.remove(query[1])
        else:
            initial_set.add(query[1])

        answer = total_cleanup(queries[:queries.index(query)], initial_set)
        result.append(answer)

    return '\n'.join(map(str, result))

