        return (data[i][0] * data[j][0] + data[i][1] * data[j][1]) == 0

    def count_combinations(n: int, k: int, bad_pairs: set) -> int:
        if n == 0 or k == 0:
            return 1
        if k == 1 or n == 1:
            return 1
        if (n, k) in bad_pairs:
            return 0

        good_pairs = count_combinations(n - 1, k - 1, bad_pairs)
        bad_pair = count_combinations(n - 1, k, bad_pairs)
        return (good_pairs + bad_pair) % 1000000007

    bad_pairs = set()
    for i in range(N):
        for j in range(i + 1, N):
            if is_on_bad_terms(i, j):
                bad_pairs.add((min(i, j), max(i, j)))

    result = count_combinations(N, 1, bad_pairs)
    return str(result)

