def solution(stdin: str) -> str:
    H, W = map(int, input().split())
    grid = [input() for _ in range(H)]

    # Determine the initial state of the grid
    snuke_changes = 0
    for i in range(H):
        for j in range(W):
            if grid[i][j] == '.':
                snuke_changes += 1

    # Check if the game can be completed
    initial_state = 1
    for i in range(H):
        for j in range(W):
            if grid[i][j] == '#':
                initial_state |= (1 << (i*W+j))
    if initial_state & (1 << (H*W-1)):
        return -1

    # Generate all possible states of the grid
    def generate_states(state: int, steps: int) -> int:
        if steps == 0:
            return 0 if state & (1 << (H*W-1)) else 1
        elif state & (1 << (H*W-2)):
            return 0
        else:
            new_state = state
            for i in range(H):
                for j in range(W):
                    if grid[i][j] == '.':
                        new_state ^= (1 << (i*W+j))
            return generate_states(new_state, steps-1)

    # Determine the maximum possible score
    max_score = generate_states(initial_state, snuke_changes)
    return max_score

