    # Initialize the sequence with one integer
    n = [1] * k
    # Find the largest integer that satisfies the product condition
    max_n = P // k
    # Find the smallest integer that satisfies the sum condition
    min_n = (S - (k - 1) * max_n) // k
    # If the smallest integer is negative or greater than the largest integer, then no solution exists
    if min_n < 1 or max_n < min_n:
        return "NO"
    # Check if the sum condition is satisfied
    if S % k == 0:
        n[0] = (S + k - 1) // k
    else:
        n[0] = min_n
    # Iterate through the sequence, updating the product and sum conditions as needed
    for i in range(1, k):
        # If the current product is greater than the target product, then decrease the current integer
        if n[i - 1] * n[0] > P:
            n[i] = n[i - 1] - 1
        else:
            # Increase the current integer as long as it doesn't exceed the target integer
            n[i] = min(n[i - 1] + 1, max_n)
        # Update the product and sum conditions as needed
        while i > 0 and n[i] * n[i - 1] > P:
            n[i] -= 1
        n[0] = (S - sum(n[i + 1:])) // (k - i)
        if n[0] < 1:
            return "NO"
    return " ".join(map(str, n))

