                else:
                    dp[i] = (dp[i] + dp[j]) % MOD
                    dp[i] = (dp[i] + (mex([arr[i], arr[j]]) - 1)) % MOD
        return dp[-1]

    n, k = map(int, input().split())
    arr = list(map(int, input().split()))
    return count_ways(arr, k)


