    turtle_pos = (8, 8)
    diamond_pos = None
    ice_castles = []

    for i, row in enumerate(board):
        for j, cell in enumerate(row):
            if cell == 'T':
                turtle_pos = (i, j)
            elif cell == 'D':
                diamond_pos = (i, j)
            elif cell == 'I':
                ice_castles.append((i, j))

    if diamond_pos is None:
        return "No solution"

    def is_valid_move(pos: tuple) -> bool:
        x, y = pos
        if x < 0 or x >= 8 or y < 0 or y >= 8:
            return False
        if board[x][y] == 'C' or board[x][y] == 'I':
            return False
        return True

    def is_ice_castle_melted(pos: tuple) -> bool:
        x, y = pos
        return (x, y) in ice_castles

    def execute_program(program: str) -> bool:
        x, y = turtle_pos
        for move in program:
            if move == 'F':
                if is_valid_move((x + 1, y)):
                    x += 1
                else:
                    return False
            elif move == 'R':
                if x % 2 == 0:
                    if y == 0:
                        return False
                    y -= 1
                else:
                    if y == 7:
                        return False
                    y += 1
            elif move == 'L':
                if x % 2 == 0:
                    if y == 7:
                        return False
                    y += 1
                else:
                    if y == 0:
                        return False
                    y -= 1
            elif move == 'X':
                if is_ice_castle_melted((x, y)):
                    ice_castles.remove((x, y))
                else:
                    return False
            else:
                return False

        return x == diamond_pos[0] and y == diamond_pos[1]

    min_program_length = float('inf')
    for i in range(4):
        for j in range(4):
            for k in range(4):
                for l in range(4):
                    program = [chr(ord('A') + i), chr(ord('A') + j), chr(ord('A') + k), chr(ord('A') + l)]
                    program_length = len(program)
                    if program_length < min_program_length and execute_program(program):
                        min_program_length = program_length

    if min_program_length == float('inf'):
        return "No solution"
    else:
        return ''.join(chr(ord('A') + i) for i in range(min_program_length))

