    for weight in input_list:
        total_weights.add(int(weight))

    if not total_weights:
        return "impossible"

    total_weights = sorted(list(total_weights))
    max_weight = total_weights[-1]

    if n > 18 or max_weight > 2**28:
        return "impossible"

    def knapsack_solution(total_weights):
        dp = [False] * (max_weight + 1)
        dp[0] = True

        for weight in total_weights:
            for i in range(max_weight, -1, -1):
                if dp[i] and i + weight <= max_weight:
                    dp[i + weight] = True

        for i in range(max_weight, -1, -1):
            if dp[i]:
                return i

    knapsack_weight = knapsack_solution(total_weights)

    if not knapsack_weight:
        return "impossible"

    max_item_weight = knapsack_weight
    items_weight = [0] * n

    for weight in reversed(total_weights):
        if weight <= max_item_weight:
            items_weight[n - 1] = weight
            max_item_weight -= weight
            n -= 1

    return " ".join(map(str, items_weight))


