            else:
                rotated += sequence[i - 1]
        return rotated

    def rotate_right(sequence: str) -> str:
        rotated = ''
        for i in range(len(sequence)):
            rotated += sequence[i - 1 if i > 0 else len(sequence) - 1]
        return rotated

    def reverse(sequence: str) -> str:
