def solution(stdin: str) -> str:
    n, m = map(int, input().split())
    adj = [[] for _ in range(n)]
    for i in range(m):
        v, u, w = map(int, input().split())
        adj[v].append((u, w))
        adj[u].append((v, w))

    a = list(map(int, input().split()))

    def dijkstra(adj, src, dst, INF=10**18):
        dist = [INF] * n
        dist[src] = 0
        vis = [False] * n

        for _ in range(n):
            u = -1
            for i in range(n):
                if not vis[i] and (u == -1 or dist[i] < dist[u]):
                    u = i
            if u == -1:
                return INF
            vis[u] = True

            for v, w in adj[u]:
                if not vis[v] and dist[u] + w < dist[v]:
                    dist[v] = dist[u] + w

        return dist[dst]

    res = []
    for i in range(n):
        d = dijkstra(adj, i, n)
        res.append(d + a[i])

    return ' '.join(map(str, res))


