            return False
        if ans[row][col] == color:
            return True
        if color in colors:
            colors.remove(color)
            ans[row][col] = color
            for dx, dy in [(0, 1), (1, 0), (-1, 0), (0, -1)]:
                if paint(row + dy, col + dx, color):
                    return True
            colors.add(color)
        return False

    for i in range(1, H + 1):
        for j in range(1, W + 1):
            for color in sorted(colors, key=lambda x: color_count[x], reverse=True):
                if paint(i, j, color):
                    break

