        if direction == 'L':
            if col - 1 >= 1 and tanks[row][col-1] == -1:
                tanks[row][col-1] = tanks[row][col]
                tanks[row][col] = -1
                return True
        elif direction == 'R':
            if col + 1 <= N and tanks[row][col+1] == -1:
                tanks[row][col+1] = tanks[row][col]
                tanks[row][col] = -1
                return True
        elif direction == 'U':
            if row - 1 >= 1 and tanks[row-1][col] == -1:
                tanks[row-1][col] = tanks[row][col]
                tanks[row][col] = -1
                return True
        elif direction == 'D':
            if row + 1 <= N and tanks[row+1][col] == -1:
                tanks[row+1][col] = tanks[row][col]
                tanks[row][col] = -1
                return True
        return False

    def dfs(tanks, depth, target_depth):
        if depth == target_depth:
            return 0

        min_moves = float('inf')
        for row in range(N):
            for col in range(N):
                if tanks[row][col] != -1:
                    for direction in ['L', 'R', 'U', 'D']:
                        if is_valid(tanks, row, col, direction):
                            tanks[row][col] = -1
                            moves = dfs(tanks, depth+1, target_depth)
                            tanks[row][col] = tanks[row][col] if direction == 'L' else 0
                            if moves != float('inf'):
                                min_moves = min(min_moves, 1 + moves)
                            tanks[row][col] = tanks[row][col] if direction == 'R' else -1
                            if moves != float('inf'):
                                min_moves = min(min_moves, 1 + moves)
                            tanks[row][col] = tanks[row][col] if direction == 'U' else 0
                            if moves != float('inf'):
                                min_moves = min(min_moves, 1 + moves)
                            tanks[row][col] = tanks[row][col] if direction == 'D' else -1
                            if moves != float('inf'):
                                min_moves = min(min_moves, 1 + moves)
        return min_moves

    for i in range(N):
        for j in range(N):
            tanks[i][j] = tanks[i][j] if tanks[i][j] != (i+1, j+1) else -1

    min_moves = dfs(tanks, 0, N)

    output = []
    for i in range(N):
        for j in range(N):
            if tanks[i][j] != -1:
                tank_num = tanks[i][j][0]
                tank_row, tank_col = tanks[i][j]
