    def swap(arr: list, i: int, j: int) -> None:
        arr[i], arr[j] = arr[j], arr[i]

    def move_to_pos(arr: list, num: int, pos: int) -> None:
        i = 0
        while i < len(arr) - 1 and arr[i] != num:
            i += 1
        j = i + 1
        while j < len(arr) and arr[j] != num:
            j += 1
        if j == len(arr):
            return
        swap(arr, i, pos)
        swap(arr, j, pos)

    N, *arr = stdin.split('\n')
    N = int(N)
    arr = list(map(int, arr))

    swaps = [0] * N
    for phase in range(1, N + 1):
        if phase % 2 == 1:
            move_to_pos(arr, phase, phase - 1)
            swaps[phase - 1] += 1
        else:
            move_to_pos(arr, N - phase + 1, phase - 1)
            swaps[phase - 1] += 1

    result = '\n'.join(map(str, swaps))
    return result

