    def create_recurrence_matrix(a: list, x: list) -> tuple:
        n = len(a)
        matrix = [[0 for _ in range(n)] for _ in range(n)]
        for i in range(n):
            matrix[i][i] = 1
        for i in range(1, n):
            for j in range(i):
                matrix[i][j] = -a[i] * matrix[i][j-1] - a[j] * matrix[i][j]
                for k in range(j):
                    matrix[i][j] += a[j] * a[k] * matrix[i][k] * matrix[j][k]
        return matrix, x

    def multiply_matrices(a: list, b: list) -> list:
        n = len(a)
        result = [[0 for _ in range(n)] for _ in range(n)]
        for i in range(n):
            for j in range(n):
                for k in range(n):
                    result[i][j] += a[i][k] * b[k][j]
        return result

    def power_matrix(matrix: list, exponent: int) -> list:
        if exponent == 1:
            return matrix
        elif exponent % 2 == 0:
            half_power = power_matrix(matrix, exponent // 2)
            return multiply_matrices(half_power, half_power)
        else:
            return multiply_matrices(matrix, power_matrix(matrix, exponent - 1))

    def calculate_x_t(matrix: list, x: list, t: int) -> int:
        return sum(matrix[i][0] * x[i] for i in range(len(x))) % 1000000007

    def process_queries(matrix: list, x: list, queries: list) -> str:
        result = ""
        for t, m in queries:
            result += str(calculate_x_t(matrix, x, t) % m) + "\n"
        return result

    input_list = stdin.split("\n")
    n = int(input_list[0])
    a = list(map(int, input_list[1].split()))
    x = list(map(int, input_list[2].split()))
    queries = [(int(t), int(m)) for t, m in [tuple(map(int, input_list[i].split())) for i in range(3, len(input_list))]]

    recurrence_matrix, initial_values = create_recurrence_matrix(a, x)
    result_matrix = power_matrix(recurrence_matrix, n)
    result = process_queries(result_matrix, initial_values, queries)

    return result


