        min_dist = float('inf')
        min_ind = -1
        for j in range(n):
            if j in visited:
                continue
            if i == j:
                continue
            d = dist(xy[i], xy[j])
            if d < min_dist:
                min_dist = d
                min_ind = j
        return min_ind, min_dist

    def find_connection(x, y, visited, e):
        if len(visited) == e:
            return 0
        min_len = float('inf')
        for i in range(n):
            if i in visited:
                continue
            min_ind, min_dist = find_nearest(x, y, visited, i)
            if min_dist >= min_len:
                continue
            visited.add(i)
            min_len = max(min_len, min_dist + find_connection(xy[min_ind][0], xy[min_ind][1], visited, e))
            visited.remove(i)
        return min_len

    def total_length(cables):
        return sum([dist(xy[a-1], xy[b-1]) for a, b in cables])

    def min_length(cables, x, y):
        visited = set(range(1, n+1))
        for a, b in cables:
            visited.remove(a)
            visited.remove(b)
        min_len = find_connection(x, y, visited, e)
        return min_len

    min_total_len = float('inf')
