    start_player = stdin[0]
    N = int(stdin[1])
    cuboids = [tuple(map(int, stdin[i+2].split())) for i in range(N)]

    # Initialize players' cuboids
    ruben_cuboids = []
    albert_cuboids = []

    for x, y, z in cuboids:
        ruben_cuboids.append((x, y, z))
        albert_cuboids.append((x, y, z))

    # Play game
    players = {'RUBEN': ruben_cuboids, 'ALBERT': albert_cuboids}
    current_player = start_player
    while len(ruben_cuboids) > 0 and len(albert_cuboids) > 0:
        if current_player == 'RUBEN':
            current_player = 'ALBERT'
        else:
            current_player = 'RUBEN'

        # Get move from current player
        moves = []
        for i in range(len(players[current_player])):
            x, y, z = players[current_player][i]
            if x > 1:
                moves.append((x-1, y, z, current_player))
            if y > 1:
                moves.append((x, y-1, z, current_player))
            if z > 1:
                moves.append((x, y, z-1, current_player))

        # Apply move
        if len(moves) == 0:
            # No valid move, remove all cuboids
            players[current_player] = []
            continue

        # Sort moves by highest score
        moves.sort(key=lambda x: x[0]*x[1]*x[2], reverse=True)

        # Apply best move
        best_move = moves[0]
        x, y, z, player = best_move
        for i in range(len(players[player])):
            if x <= players[player][i][0] <= x+1 and y <= players[player][i][1] <= y+1 and z <= players[player][i][2] <= z+1:
                players[player].pop(i)
                break

    # Determine winner
    if len(ruben_cuboids) == 0:
        return 'ALBERT'
    else:
        return 'RUBEN'

