    from collections import defaultdict
    from heapq import heappush, heappop
    def dijkstra(graph, start, end):
        queue = [(0, start, [])]
        visited = set()
        while queue:
            (cost, current, path) = heappop(queue)
            if current not in visited:
                visited.add(current)
                path = path + [current]
                if current == end:
                    return cost, path
                for neighbor, weight in graph[current].items():
                    heappush(queue, (cost + weight, neighbor, path))
        return None

    def shortest_path(graph, start, end):
        result, _ = dijkstra(graph, start, end)
        return result

    def pizza_plan(n, m, c, connections):
        graph = [defaultdict(int) for _ in range(n)]
        for s, t in connections:
            graph[s][t] = 1
        result = 0
        for i in range(n):
            path = shortest_path(graph, 0, i)
            if path is not None:
                result += sum(c[j] * 1/2**(j-1) for j in range(1, len(path)+1))
        return result

    lines = stdin.split('\n')
    n, m = map(int, lines[0].split())
    c = list(map(int, lines[1].split()))
    connections = [tuple(map(int, line.split())) for line in lines[2:]]

