    input_list = stdin.split('\n')
    offset = int(input_list[0])
    message = input_list[1]
    num_steps = int(input_list[2])

    vowels = {'a', 'e', 'i', 'o', 'u', 'y'}

    def count_consonants_and_vowels(text: str) -> tuple:
        consonants = 0
        vowels_count = 0
        for char in text:
            if char.isalpha():
                if char.lower() in vowels:
                    vowels_count += 1
                else:
                    consonants += 1
        return consonants, vowels_count

    def rotate_char(char: str, offset: int) -> str:
        if char.isalpha():
            char_code = ord(char.lower()) - ord('a')
            new_char_code = (char_code + offset) % 26
            return chr(new_char_code + ord('a'))
        else:
            return char

    def encode_message(message: str, offset: int, num_steps: int) -> str:
        encoded_message = message
        for _ in range(num_steps):
            encoded_message = ''.join(rotate_char(char, offset) for char in encoded_message)
        return encoded_message

    bad_steps = 0
    good_steps = 0

    for step in range(num_steps):
        encoded_message = encode_message(message, offset, step + 1)
        consonants, vowels_count = count_consonants_and_vowels(encoded_message)
        if consonants >= vowels_count:
            bad_steps += 1
        else:
            good_steps += 1

    if bad_steps > good_steps:
        return 'Boris'
    else:
        return 'Colleague'

