    N = int(stdin.split('\n')[0])
    categories = {}
    for i in range(1, N + 1):
        line = stdin.split('\n')[i].split(' ')
        categories[line[0]] = (int(line[1]), set(line[2:]))

    # Classify statement
    statement = [set(word.lower() for word in line.split(' ')) for line in stdin.split('\n')[N + 1:]]
    matches = {}
    for category, (_, words) in categories.items():
        matches[category] = sum(1 for sentence in statement for word in sentence if word in words)

    # Output suggestion
    suggestions = [category for category, score in matches.items() if score > 0]
    suggestions.sort(key=lambda x: (-matches[x], x))
    return '\n'.join(suggestions)

