def solution(stdin: str) -> str:
    def is_zigzag(seq):
        n = len(seq)
        if n < 2:
            return True
        increasing = seq[0] <= seq[1]
        for i in range(1, n - 1):
            if increasing:
                if seq[i] > seq[i + 1]:
                    return False
            else:
                if seq[i] < seq[i + 1]:
                    return False
            increasing = not increasing
        return True

    def longest_zigzag(seq):
        n = len(seq)
        if n < 2:
            return n
        longest = 1
        for i in range(1, n):
            if is_zigzag(seq[:i + 1]):
                longest = max(longest, i + 1)
        return longest

    n = int(stdin.splitlines()[0])
    seq = [int(line) for line in stdin.splitlines()[1:]]
    result = longest_zigzag(seq)
    return str(result)

