    k = int(input_list[1])

    n = len(s)

    # calculate the number of swaps required to sort the string
    count_swaps = 0
    for i in range(n):
        for j in range(i + 1, n):
            if abs(s.index(s[i]) - s.index(s[j])) >= k:
                count_swaps += 1

    # calculate the number of swaps required to sort the string without considering the k condition
    count_swaps_no_k = 0
    for i in range(n):
        for j in range(i + 1, n):
            if s.index(s[i]) > s.index(s[j]):
                count_swaps_no_k += 1

    # if the number of swaps is greater than or equal to the number of swaps required to sort the string without considering k, then it is possible to sort the string increasingly
    if count_swaps >= count_swaps_no_k:
        return "Yes"
    else:
        return "No"

def solution(stdin: str) -> str:
    n, k = map(int, stdin.split()[0:2])
    A = list(map(int, stdin.split()[2:]))

    def find_max_sum(A, i, j):
        if i == j:
            return A[i]
        else:
            return max(A[i] + find_max_sum(A, i+1, j), find_max_sum(A, i+1, j))

    def find_k_max_sum(A, i, j, k):
        if k == 1:
            return find_max_sum(A, i, j)
        else:
            max_left = find_k_max_sum(A, i, j, k-1)
            max_right = find_k_max_sum(A, i+1, j, k-1)
            return max(max_left + find_max_sum(A, i+1, j), max_right)

    return str(find_k_max_sum(A, 0, n-1, k))

