    n = int(input())
    nums_to_indices = {}
    indices_to_nums = {}

    for i in range(1, n+1):
        m, *nums = map(int, input().split())
        nums_to_indices[tuple(nums)] = i
        indices_to_nums[i] = nums

    def find_connection(p, q, visited):
        if p in visited or q in visited or p == q:
            return False
        visited.add(p)
        for num in indices_to_nums[p]:
            if (num, q) in nums_to_indices:
                return True
            if find_connection(nums_to_indices[(num, q)], q, visited):
                return True
        return False

    for i in range(1, n):
        for j in range(i+1, n+1):
            if find_connection(i, j, set()):
                return f"{i} {j} {next(num for num in indices_to_nums[i] if num in indices_to_nums[j])}"
    return "impossible"

