    n, m = int(n), int(m)
    grid = [list(row) for row in grid.split()]

    def find_neighbors(i, j):
        neighbors = []
        if i > 0:
            neighbors.append((i - 1, j))
        if i < n - 1:
            neighbors.append((i + 1, j))
        if j > 0:
            neighbors.append((i, j - 1))
        if j < m - 1:
            neighbors.append((i, j + 1))
        return neighbors

    def count_rings(row, col):
        visited = set()
        queue = [(row, col)]
        while queue:
            i, j = queue.pop(0)
            if (i, j) in visited:
                continue
            visited.add((i, j))
            for neighbor in find_neighbors(i, j):
                if grid[neighbor[0]][neighbor[1]] == 'T':
                    queue.append(neighbor)
        return len(visited)

    for i in range(n):
        for j in range(m):
            if grid[i][j] == 'T':
                grid[i][j] = count_rings(i, j)

    for i in range(n):
        for j in range(m):
            if grid[i][j] is None:
                grid[i][j] = '.'
            elif grid[i][j] < 10:
                grid[i][j] = f'{grid[i][j]:2}'
            else:
                grid[i][j] = f'{grid[i][j]:3}'

    output = '\n'.join([''.join(row) for row in grid])
