                if not pumpkin_died:
                    new_pumpkins[position] = new_days
        return new_pumpkins

    def simulate(pumpkins: Dict[Tuple[int, int], int], n: int, days: int) -> str:
        output = []
        for _ in range(days):
            pumpkins = grow(pumpkins, n)

        for position, days in pumpkins.items():
