                if i != j:
                    incongruousity = max(get_changes(alternatives[i], alternatives[j]),
                                          get_changes(alternatives[j], alternatives[i]))
                    if incongruousity < least_incongruous:
                        least_incongruous = incongruousity
        return least_incongruous

    def get_changes(alt1: str, alt2: str) -> int:
        parts1 = alt1.split(', ')
        parts2 = alt2.split(', ')
        changes = 0
        for i in range(len(parts1)):
            if parts1[i] != parts2[i]:
                changes += 1
        return changes

