    alternatives = stdin.split('\n')[2:]
    least_incongruous = find_least_incongruous(alternatives)
    least_incongruous_alternatives = [alt for alt in alternatives if get_changes(alt, alternatives[0]) == least_incongruous]
    return ', '.join(least_incongruous_alternatives)

